ALTER TABLE `probes` ADD `probe_perfdata` VARCHAR(2048) NULL DEFAULT NULL AFTER `probe_output`;
ALTER TABLE `probes` ADD `probe_data` TEXT NULL DEFAULT NULL AFTER `probe_perfdata`;
CREATE TABLE `probe_metrics` (  `metric_id` int(11) NOT NULL AUTO_INCREMENT,  `probe_id` int(11) NOT NULL,  `metric_label` varchar(255) NOT NULL,  `metric_type` enum('GAUGE','COUNTER') NOT NULL DEFAULT 'GAUGE',  `metric_last_value` float DEFAULT NULL,  `metric_uom` varchar(32) DEFAULT NULL,  PRIMARY KEY (`metric_id`),  UNIQUE KEY `probe_id_metric_label` (`probe_id`,`metric_label`),  KEY `probe_id` (`probe_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;
CREATE TABLE `cache` ( `cache_key` varchar(255) NOT NULL,  `cache_value` LONGTEXT,  `cache_expires` timestamp NULL DEFAULT NULL,  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  PRIMARY KEY (`cache_key`),  KEY `cache_expires` (`cache_expires`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;
